import React, { Component } from 'react';

class AccessForm extends Component {
    state = {
        passphrase: '',
        handle: '',

    }

    onChange = e => {
        this.setState({ [e.target.name]: e.target.value })
    }

    render() {
        return (
            <div className="container">
                <h1 className="mt-3">Get Started</h1>
                <form onSubmit={this.onSubmit} className="m-1 card bg-light">
                    <label htmlFor="keystore">Keystore File</label>
                    <input type="file" name="keystore" id="keystore" required/>

                    <label htmlFor="passphrase">Passphrase</label>
                    <input type="text" onChange={this.onChange} name="passphrase" id="passphrase" placeholder="Passphrase" required/>

                    <label htmlFor="handle">Telegram Handle</label>
                    <input type="text" onChange={this.onChange} id="handle" name="handle" placeholder="@elonmusk" required/>
                    <button type="submit" className="btn btn-success mt-b">Get Started!</button>
                </form>
            </div>
        )
    }
}

export default AccessForm;
